import React from "react";
import { Address, AddressForm } from "../../components/addressform";
import { useMeQuery, useUpdateUserDataMutation, useUpdateUserNameMutation, useUpdateUserAddressMutation } from "../../generated/graphql";
import { ErrorMessage } from "../../pages/pageframe/global/Messages/ErrorMessage";
import { NameForm, NameData } from "../../components/nameform";
import { LoadingAnimation } from "../../pages/pageframe/global/Messages/LoadingAnimation";

interface Props {

}

export const PersonalDataEditor: React.FC<Props> = () => {

    const {data, loading, error} = useMeQuery();
    const [username] = useUpdateUserNameMutation();
    const [useraddress] = useUpdateUserAddressMutation();

    if(error){
        return (<ErrorMessage error={error} show={true}/>)
    }


    const handleSaveAddress = (address: Address) => {
        useraddress({
            variables: {
                useraddress: address
            }
        })
    }

    const handleSaveName = (name: NameData) => {
        username(
            {
                variables: {
                    username: name
                }
            }
        )

    }


    if(data && data.me){
        const user = data.me;

        const address: Address = {
            streetName: "",
            streetNumber: "",
            postalCode: "",
            town: ""  
        }

        const name: NameData ={
            gender: "male",
            first_name: "Sven",
            surename: "Lehneke"
        }



        return (
            <div>
                <NameForm values={name} onSubmit={handleSaveName}/>
                <AddressForm values={address} onSubmit={handleSaveAddress}/>
            </div>

        )
    }
    return (
        <LoadingAnimation message={"Daten werden geladen"} />
    )
}
