import React from 'react';
import {BrowserRouter, Switch, Route} from 'react-router-dom'
import { Register } from '../pageframe/global/user/Register'
import { LoginForm } from '../pageframe/global/user/LoginForm'
import { Home } from '../homepage/Homepage'
import {BookingRouter } from '../booking/BookingRouter'
import { Header } from './Header';
import { Footer } from './footerarea/FooterArea';
import { ThemeProvider, createMuiTheme } from '@material-ui/core';
import { LoadingAnimation } from '../pageframe/global/LoadingAnimation';
import { AdminPage } from '../admin/AdminPage';

interface Props {
  serverConnected: boolean
}


export const Routes: React.FC<Props> = (props) => {


  const theme = createMuiTheme({
    palette: {
      primary: {
        main: '#4caf50'
      },
      secondary: {
        main: '#f57c00'
      },
    },
  });

  var content = <LoadingAnimation message={'Verbindung zum Server wird hergestellt ...'}/>
  if(props.serverConnected){
      content = (
        <Switch>
        <Route exact path="/" component={Home}></Route>
        <Route exact path="/register" component={Register}></Route>
        <Route exact path="/login" component={LoginForm}></Route>
        <Route exact path="/booking" component={BookingRouter}></Route>
        <Route exact path="/admin" component={AdminPage}></Route>
        <Route exact path="/admin/addlocation" component={AdminPage}></Route>
      </Switch>
      )
    }



   return (<BrowserRouter>
   <ThemeProvider theme={theme}>
    <div>
      <Header />     
        {content}
      <Footer />
    </div>
    </ThemeProvider>
   </BrowserRouter>);
}
