import React, { useState } from 'react'
import { Property, useSavePropertyMutation, usePropertiesQuery } from '../../../../generated/graphql'
import { EditorForm } from '../../form/EditorForm';
import { useParams } from 'react-router-dom';
import { PictureUpload } from '../../imagehandler/PictureUpload';
import { Container } from '@material-ui/core';
import { ResponseMessage } from '../../../pageframe/global/Messages/ResponseMessage';
import { Editor } from '../../form/Editor';
import { MessageView } from '../../../pageframe/global/Messages/MessageView';


interface Props {

}




export const newProperty: Property = { 
                    id: 0,
                    title: "",
                    subtext: "",
                    location: "",
                    lat: 0.0,
                    long: 0.0,
                    beds_adult: 0,
                    beds_kids: 0,
                    beds_tent: 0,
                    images: [],   
                }

export const PropertyEditor: React.FC<Props> = (props) => {

    const { propertyId } = useParams()

    const [showMessage, setshowMessage] = useState(false);

    const {data, loading, error} = usePropertiesQuery();

    if(loading){
        console.log('loading') 
    }

    if(error){
        console.log(error) 
    }

    var property = newProperty;
    if(data && data.allProperties && propertyId) {
        var selectedProp = data.allProperties
            .find((prop) => {
                    return (prop.id == parseInt(propertyId))
            })  
        if(selectedProp){
            property = selectedProp;
        }
    }

    const [saveProperty] = useSavePropertyMutation();

    async function onSubmit(values: Property) {

        values.__typename = undefined;
        values.lat = parseFloat(values.lat+"");
        values.long = parseFloat(values.long+"");
        values.beds_adult = parseFloat(values.beds_adult+"");
        values.beds_kids = parseFloat(values.beds_kids+"");
        values.beds_tent = parseFloat(values.beds_tent+"");

        var result = await saveProperty({
            variables: {
                property: values
            }
        });
        if(result){
            setshowMessage(true)
        }
    }

    return(
        <Editor 
            entity={property} 
            store={onSubmit} 
            entityId={property.id}
            imageuploadurl={"http://localhost:4000/upload/property/image"}>     
            <MessageView severity={"success"} message={"Die Daten wurden gespeichert"} show={showMessage} />
        </Editor>
    )



}
