import { Property } from "../../generated/graphql";
import { Moment } from "moment";
import React, { useState } from "react";

type Booking = {
    selectedProperty: Property | undefined,
    arrivalDate: Moment | undefined,
    departureDate: Moment | undefined,
    setSelectedPropterty: (selectedProerty: Property | undefined) => void,
    setArrivalDate: (arrivalDate: Moment | undefined) => void,
    setDepartureDate: (deoartureDate: Moment | undefined) => void
}

const test: Property | undefined = undefined;
const funkt = (test: Property) => {return test}

const emptyBooking: Booking = {
    selectedProperty: undefined,
    arrivalDate: undefined,
    departureDate: undefined,
    setSelectedPropterty: (undefined) => {},
    setArrivalDate: (undefined) => {return undefined},
    setDepartureDate: (undefined) => {return undefined}
}

export const BookingContext = React.createContext<Booking>(emptyBooking);

export const useBooking = (): Booking => {
    const [selectedProperty, setSelectedProp] = useState();
    const [arrivalDate, setArrivalD] = useState();
    const [departureDate, setDepartureD] = useState();

    const setSelectedPropterty = () => React.useCallback((property: Property | undefined): void => {
        setSelectedProp(property)
    }, [])

    const setArrivalDate = () => React.useCallback((arrival: Moment | undefined): void => {
        setArrivalD(arrival);
    }, [])

    const setDepartureDate = () => React.useCallback((departure: Moment | undefined): void => {
        setDepartureD(departure);
    }, [])

    return {
        selectedProperty,
        arrivalDate,
        departureDate,
        setSelectedPropterty,
        setArrivalDate,
        setDepartureDate
    }
}
