import React from 'react'
import DateFnsUtils from '@date-io/date-fns';
import { Grid, Button } from '@material-ui/core';
import {
    MuiPickersUtilsProvider,
    KeyboardDatePicker,
  } from '@material-ui/pickers';

interface Props {
    arrivalDate: any
    departureDate: any
    bookingListener: any
    cancleListener: any
    booking: Boolean
    property: any
}


export const MyBooking:  React.FC<Props> = (Props) => {

    const d = new Date(Date.now());
    const [arrival, setArrival] = React.useState<Date | null>(Props.arrivalDate);
    const [departure, setDeparture] = React.useState<Date | null>(Props.departureDate);

    const handleStartDateChange = (date: Date | null) => {
      setArrival(date);
      };

      const handleEndDateChange = (date: Date | null) => {
        setDeparture(date);
      };

      const saveBooking = () => {
          Props.bookingListener(arrival?.getTime(), departure?.getTime())
      } 

    return (
        <div>
           <MuiPickersUtilsProvider utils={DateFnsUtils}>
            <Grid container>
                <KeyboardDatePicker
                disableToolbar
                variant="inline"
                format="dd.MM.yyyy"
                margin="normal"
                id="date-picker-inline"
                label="Ankunft"
                value={arrival}
                onChange={handleStartDateChange}
                KeyboardButtonProps={{
                    'aria-label': 'change date',
                }}
                />
                <KeyboardDatePicker
                margin="normal"
                id="date-picker-inline"
                label="Abreise"
                format="dd.MM.yyyy"
                value={departure}
                onChange={handleEndDateChange}
                KeyboardButtonProps={{
                    'aria-label': 'change date',
                }}
                />

                <Button onClick={saveBooking}>
                    Reservieren
                </Button>
            </Grid>
            </MuiPickersUtilsProvider>
            <div>
              {Props.property.title}
              </div>
        </div>
    )

}
