import React, { useState } from 'react'
import { newProperty } from './PropertyEditor';
import { Formik } from 'formik';
import * as Yup from 'yup'

interface Props {
    propertyId: number
}

export const PictureUpload: React.FC<Props> = () => {

    const [selectedProperty, setSelectedProperty] = useState(newProperty)


    return(
        <Formik 
          initialValues={{ file: {name: ""} }}
          onSubmit={(values) => {  
            if(values && values.file){
                var filename = values?.file?.name;
                if(filename)
                alert(
                    JSON.stringify(
                      { 
                        fileName: filename, 
                      },
                      null,
                      2
                    )
                  );
            } 
          }} 
          validationSchema={Yup.object().shape({
            file: Yup.mixed().required(),
          })}
          render={({ values, handleSubmit, setFieldValue }) => {
            return (
              <form onSubmit={handleSubmit}>
                <div className="form-group">
                  <input id="file" name="file" type="file" onChange={(event) => {
                      if(event?.currentTarget && event.currentTarget.files)
                    setFieldValue("file", event.currentTarget.files[0]);
                  }} className="form-control" />

                </div>
                <button type="submit" className="btn btn-primary">submit</button>
              </form>
            );
          }} />
    )
}
