import React from "react";
import { Paper, Button, Typography, withStyles, List, ListItem } from "@material-ui/core";

interface Props {
    offer: { price: number,
        durationMonth: number,
        features: string[] }
    onOfferClicked: (offer: any) => void
}

export const PriceTag = withStyles(
    {
      root: {
        marginTop: '8px',
        width: '100%',
        fontSize: '64px',
        fontWeight: "lighter",
        textAlign: 'center'
      }

    }
  )(Typography)

  export const ActionButton = withStyles(
    {
      root: {
        justifyContent: 'center'
      }

    }
  )(Button)

  export const MonthTag = withStyles(
    {
      root: {
        marginBottom: '16px',
        width: '100%',
        fontSize: '24px',
        textAlign: 'center'
      }

    }
  )(Typography)

export const OfferView: React.FC<Props> = (props) => {

    const offer = props.offer;


   return (
    <Paper variant="outlined" style={{justifyContent: 'center'}}>
        <PriceTag color="primary">
            {offer.price}<span style={{fontSize: '14px'}}>€/m</span>
        </PriceTag>

        <MonthTag>
            für {offer.durationMonth} Monate 
        </MonthTag>

        <List style={{width: '100%', margin: 'auto'}}>
            {offer.features.map((x) =>{
                return (
                    <ListItem key={x}>{x}</ListItem>
                )
            }
            )}
        </List>
        <div style={{padding: '8px', textAlign: 'center', marginTop: '16px'}}>
            <ActionButton variant="contained" color="secondary">loslegen</ActionButton>
        </div>   
    </Paper>
   )
}
