import React, { useContext, useState, useEffect } from "react";
import { CheckoutContext, CheckoutContextProvider } from "../../context/checkoutcontext/CheckoutContext";
import { CheckOutFlow } from "./CheckOutFlow";
import { ProductOffers } from "../productoffer/ProductOffers";
import { CheckoutSummary } from "../checkoutsummary/CheckoutSummary";
import { Payment } from "../../components/paymentformcard/Payment";
import { AddressEditor } from "../addresseditor";
import { CheckoutStepContext, CheckoutStepContextProvider } from "../../context/checkoutcontext/CheckoutStepContext";


interface Props {
}

export const CheckOutContainer: React.FC<Props> = (Props) => {

   const {step, setstep} = useContext(CheckoutStepContext)
    const [next, setnext] = useState()


    const nextStep = () => {      
        setstep(step + 1)
        console.log("nextStep " + step);
    }

    useEffect(() => {
        setnext(step + 1)
    }, [step])


    const steps = [
        "Adressdaten",
        "Bezahldaten",
        "Bestellung",
        "Zusammenfassung"
    ]


   return ( 
       <CheckoutStepContextProvider>
        <CheckoutContextProvider>
            <CheckOutFlow steps={steps}>
                <AddressEditor onDone={nextStep}/>
                <ProductOffers/>
                <Payment />     
                <CheckoutSummary />
            </CheckOutFlow>
        </CheckoutContextProvider>
    </CheckoutStepContextProvider>
    )
}
