import React from 'react'
import { Button, TextField, Grid } from '@material-ui/core'
import { Formik , Form} from 'formik'
import styles from "./AddressForm.module.css"
import * as Yup from 'yup'


export interface Address {
    streetname: string
    streetnumber: string
    town: string
    postal: string
}

interface Props {
    values: Address;
    onSubmit: (values: Address) => void;
}

export const AddressForm: React.FC<Props> = (props) => {
    return (
        <Formik initialValues={props.values} 
            onSubmit={props.onSubmit} 
            enableReinitialize={true}
            validationSchema={
                Yup.object().shape({
                    streetname: Yup.string().required("Required"),
                    streetnumber: Yup.string().required("Required"),
                    postal: Yup.string().min(8).required("Required"),
                    town: Yup.string().required("Required"),
              })}>
            {({values, handleChange, handleBlur}) => (
                <Form>
                    <div className={styles.row_elements}>
                        <TextField
                        style={{flex: 1}} 
                        name={values.streetname}
                        label={"Straße"}
                        value={values.streetname} 
                        onChange={handleChange}
                        onBlur={handleBlur}
                        />
                        <TextField 
                        style={{width: "80px"}}
                        name={" "+values.streetnumber}
                        label={"Nr."}
                        value={values.streetnumber} 
                        onChange={handleChange}
                        onBlur={handleBlur}/>
                    </div>
                    <div className={styles.row_elements}>
                        <TextField 
                        style={{width: "80px"}}
                        name={values.postal}
                        label={"PLZ"}
                        value={values.postal} 
                        onChange={handleChange}
                        onBlur={handleBlur}
                        />
                        <TextField 
                        style={{flex: 1}}
                        name={" "+values.town}
                        label={"Ort"}
                        value={values.town} 
                        onChange={handleChange}
                        onBlur={handleBlur}/>
                    </div>      
                <Button variant="outlined" type="submit">
                    speichern
                </Button>
                </Form>  
            ) }           
        </Formik>
    )
}
