import React from "react";
import { Map, Marker, TileLayer} from 'react-leaflet'
import { PropObject } from "../../generated/graphql";

interface Props {
     properties: PropObject[]
}



export const MapView: React.FC<Props> = (props) => {

     // const firstProp = props.properties[0];
     console.log(props.properties)
     const position = {lat: 51.662, lng: 0.778};
     const style = {
     width: '100%',
     height: '300px'
   };

   return (
        <Map style={style} center={position} zoom={12}>
          <TileLayer
               url="https://{s}.tile.openstreetmap.org/{z}/{x}/{y}.png"
               attribution='&copy; <a href="http://osm.org/copyright">OpenStreetMap</a> contributors'
          />
          {props.properties.map(prop => (
               <Marker
                    key={prop.id}
                    position={[
                    prop.lat,
                    prop.long
                    ]}
                    onClick={() => {

                    }}
               />
               ))}

        </Map>
   )
}
