import React from 'react'
import { useMyBookingQuery, useDeleteBookingMutation, useCreateMyBookingMutation} from '../../../generated/graphql';
import { DateSelector } from './DateSelector';
import { PropertySelectionView } from './PropertySelectionView';
import { BookingButton } from './ BookingButton';

interface Props {
    bookingActiveListener: any
    propertySelected: any
}


export const MyBooking:  React.FC<Props> = (Props) => {

    const {data, loading, error} = useMyBookingQuery();
    const [Booking] = useCreateMyBookingMutation();
    const [deleteBooking] = useDeleteBookingMutation()

    if(loading){
      console.log(loading)
    }

    if(error){
      console.log(error)
    }

    var propertySelected: any = Props.propertySelected;
    var activeBooking: boolean = false;
    var arrival = Date.now();
    var departure = Date.now() + 1700000; 


    if(data && data?.myBooking){
      arrival = data.myBooking.from;
      departure = data.myBooking.to;
        if(data.myBooking.property){
          propertySelected = data.myBooking.property
          activeBooking = true;
          Props.bookingActiveListener(propertySelected.id)
        } 
    }

    const bookingButtonPressed = () => {
      if(activeBooking){
        if(data && data.myBooking){
          const result = deleteBooking({
            variables: {
              bookingId: data.myBooking.id
            }
          })
          console.log(result)         
        }   
      } else{
        if(propertySelected && arrival && departure){
          const result = Booking(
            { 
              variables: {
                  from: arrival,
                  to: departure,
                  propertyId: propertySelected.id
              }
            })
            console.log(result)
        }
      }
    }




    const dateChangeListener = (dates: any) => {
      console.log("dates changed: " + dates.arrival + " " + dates.departure);
    }

    return (
        <div>
          <DateSelector dates={{arrival: arrival, departure: departure}} dateChangeListener={dateChangeListener} />
          <PropertySelectionView property={propertySelected} />
          <BookingButton aktiveBooking={activeBooking} buttonPressedListener={bookingButtonPressed}/>
        </div>
    )

}
