import React from "react";
import { Paper, Button, Typography, withStyles } from "@material-ui/core";

interface Props {
    product: Product
    onOfferClicked: (productId: string) => void
}

export const PriceTag = withStyles(
    {
      root: {
        marginTop: '8px',
        width: '100%',
        fontSize: '64px',
        fontWeight: "lighter",
        textAlign: 'center'
      }

    }
  )(Typography)

  export const ActionButton = withStyles(
    {
      root: {
        justifyContent: 'center'
      }

    }
  )(Button)

  export const MonthTag = withStyles(
    {
      root: {
        marginBottom: '16px',
        width: '100%',
        fontSize: '24px',
        textAlign: 'center'
      }

    }
  )(Typography)

export const OfferView: React.FC<Props> = (props) => {

    const offer = props.offer;


   return (
    <Paper variant="outlined" style={{justifyContent: 'center'}}>
        <PriceTag color="primary">
            {offer.price}<span style={{fontSize: '14px'}}>€/m</span>
        </PriceTag>

        <MonthTag>
            für {offer.durationMonth} Monate 
        </MonthTag>

        <ul style={{width: '95%', margin: 'auto', fontSize: '14px'}}>
            {offer.features.map((x) =>{
                return (

                    <li key={x}> {x}
                    </li>
                )
            }
            )}
        </ul>
        <div style={{padding: '8px', textAlign: 'center', marginTop: '16px'}}>
            <ActionButton variant="contained" color="secondary" onClick={props.onOfferClicked()}>jetzt bestellen</ActionButton>
        </div>   
    </Paper>
   )
}
