import React from 'react'
import { Button, TextField, Select, MenuItem } from '@material-ui/core'
import { Formik , Form} from 'formik'
import styles from "./NameForm.module.css"
import * as Yup from 'yup'
import { SubmitButton } from '../formsubmitbutton'


export interface NameData {
    gender: "male" | "female" | "else"
    firstName: string
    surname: string
}

interface Props {
    values: NameData;
    onSubmit: (values: NameData) => void;
}

export const NameForm: React.FC<Props> = (props) => {
    return (
        <Formik initialValues={props.values} 
            onSubmit={props.onSubmit} 
            validationSchema={
                Yup.object().shape({
                    firstName: Yup.string().required("Required"),
                    surname: Yup.string().min(8).required("Required"),
              })}>
            {({values, handleChange, handleBlur}) => (
                <Form>
                    <div className={styles.row_elements}>
                        <Select
                        style={{width: "160px", justifySelf: "flex-start"}} 
                        name={values.gender}
                        label={""}
                        id={"gender"}
                        value={values.gender} 
                        onChange={handleChange}
                        onBlur={handleBlur}
                        >
                            <MenuItem value={"male"}>Mann</MenuItem>
                            <MenuItem value={"female"}>Frau</MenuItem>
                            <MenuItem value={"else"}>Sonstige</MenuItem>
                        </Select>
                    </div>
                    <div className={styles.row_elements}>
                        <TextField 
                        style={{flex: 1}}
                        name={values.firstName}
                        label={"Vorname"}
                        id={"firstName"}
                        value={values.firstName} 
                        onChange={handleChange}
                        onBlur={handleBlur}
                        />
                        <TextField 
                        style={{flex: 1, marginLeft: "40px"}}
                        name={" "+values.surname}
                        id={"surname"}
                        label={"Nachname"}
                        value={values.surname} 
                        onChange={handleChange}
                        onBlur={handleBlur}/>
                    </div>      
                    <SubmitButton label={"übernehmen"} />
                </Form>  
            ) }           
        </Formik>
    )
}
