import React, { useState } from "react"
import "react-dates/initialize"
import "react-dates/lib/css/_datepicker.css"
import {DateRangePicker} from "react-dates" 
import "bootstrap/dist/css/bootstrap.min.css"
import moment from "moment"



interface Props {
    dates: any
    dateChangeListener: any
}


export const DateSelector:  React.FC<Props> = (Props) => {

    const date = moment().add(7, 'd').locale('de');

    const [startDate, setStartDate] = useState(date);
    const [endDate, setEndDate] = useState(date);
    const [focusedInput, setFocusedInput] = useState();

    // const [dates, setDates] = useState({arrival: null, departure: null});

    /*
    const setDateOfArrivial = (date: any) => {
        const arrivalDate = new Date(date);
        setArrival(arrivalDate.getTime())
        Props.dateChangeListener({arrival: arrival, departure: departure});
    }

    const setDateOfDeparture = (date: any) => {
        const departureDate = new Date(date);
        setDeparture(departureDate.getTime())
        Props.dateChangeListener({arrival: arrival, departure: departure});      
    }
    */

    function onDateChange(start: any, end: any) {
        if(start){
            setStartDate(start);
        }
        if(end){
            setEndDate(end);
        }     
      }

      function onFocusChange(focusedInput: any) {
        setFocusedInput(focusedInput);
      }

      const isOutsideRange = (day: any) =>
      day.isAfter(moment()) || day.isBefore(moment().subtract(30, "days"));


    return (
            <div>

            <DateRangePicker
                startDate={startDate}
                startDateId="your_unique_start_date_id"
                endDate={endDate}
                endDateId="your_unique_end_date_id"
                onDatesChange={({ startDate, endDate }) => { onDateChange( startDate, endDate )}}
                focusedInput={focusedInput}
                displayFormat={() => "DD. MMMM YYYY"}
                onFocusChange={onFocusChange}
                isOutsideRange={isOutsideRange}/>    

        </div>
    )
}
