import React from 'react'
import { Formik } from 'formik';
import * as Yup from 'yup'
import axios from 'axios'
import { Thumb } from './ThumbnailView';


interface Props {
    propertyId: number
    url: string
}

export const PictureUpload: React.FC<Props> = (props) => {

    const onSubmitHandler = (file: any) => {
        const data = new FormData()
        data.append('myImage', file)
        data.append('prop', props.propertyId+"")
        axios.post(props.url, data, { 
            headers: {
                'Content-Type': 'multipart/form-data'
              }
        }).then((response) => {
            console.log(response)
        })
    }

    return(
        <Formik 
          initialValues={{file: null}}
          onSubmit={(values) => {  
            if(values && values.file){
                onSubmitHandler(values.file)
                console.log(values.file)

            } 
          }} 
          validationSchema={Yup.object().shape({
            file: Yup.mixed().required(),
          })}
          render={({ values, handleSubmit, setFieldValue }) => {
            return (
              <form onSubmit={handleSubmit}>
                <div className="form-group">
                  <input id="file" name="file" type="file" onChange={(event) => {
                      if(event?.currentTarget && event.currentTarget.files)
                    setFieldValue("file", event.currentTarget.files[0]);
                    console.log("test upload")
                  }} className="form-control" />
                  <Thumb tfile={values.file} ></Thumb>
                </div>
                <button type="submit" className="btn btn-primary">submit</button>
              </form>
            );
          }} />
    )
}
