import React from 'react'
import DateFnsUtils from '@date-io/date-fns';
import { Grid, Button } from '@material-ui/core';
import {
    MuiPickersUtilsProvider,
    KeyboardDatePicker,
  } from '@material-ui/pickers';

interface Props {
    arrivalDate: any
    departureDate: any
    bookingListener: any
    cancleListener: any
    booking: Boolean
    property: any
}


export const MyBooking:  React.FC<Props> = (Props) => {

    const [arrival, setArrival] = React.useState<Date | null>(Props.arrivalDate);
    const [departure, setDeparture] = React.useState<Date | null>(Props.departureDate);

    const {data, loading, error} = useMyBookingQuery();

    if(data && data?.myBooking){
        bookActiv = true;
        arrival = new Date(data.myBooking.from); 
        departure = new Date(data.myBooking.to); 
        if(data.myBooking.property){
            selectProp = data.myBooking.property;
        }
        if(selectProp){
            console.log("property loaded:" + selectProp.id)
        }      
    }


    const handleStartDateChange = (date: Date | null) => {
      setArrival(date);
      };

      const handleEndDateChange = (date: Date | null) => {
        setDeparture(date);
      };

      const saveBooking = () => {
          Props.bookingListener(arrival, departure)
      } 

      let propertyDisplay;

      if(Props.property){
          propertyDisplay = <div>{Props.property.title}</div>
      } else{
          propertyDisplay = <div></div>
      }  

    return (
        <div>
           <MuiPickersUtilsProvider utils={DateFnsUtils}>
            <Grid container>
                <KeyboardDatePicker
                disableToolbar
                variant="inline"
                format="dd.MM.yyyy"
                margin="normal"
                id="date-picker-inline"
                label="Ankunft"
                value={arrival}
                onChange={handleStartDateChange}
                KeyboardButtonProps={{
                    'aria-label': 'change date',
                }}
                />
                <KeyboardDatePicker
                margin="normal"
                id="date-picker-inline"
                label="Abreise"
                format="dd.MM.yyyy"
                value={departure}
                onChange={handleEndDateChange}
                KeyboardButtonProps={{
                    'aria-label': 'change date',
                }}
                />

                <Button onClick={saveBooking}>
                    Reservieren
                </Button>
            </Grid>
            </MuiPickersUtilsProvider>
            <div>  
              {propertyDisplay}
              </div>
        </div>
    )

}
