import React, { useState } from "react";

import { Button, Stepper, Typography, StepLabel, Step, makeStyles } from "@material-ui/core";
import { LoginForm } from "../../pageframe/global/user/LoginForm";
import { PriceSection } from "./priceSectio/PriceSection";
import { SignInSection } from "./SignInSection";

interface Props {

}

const useStyles = makeStyles((theme) => ({
    root: {
      width: '100%',
    },
    button: {
      marginRight: theme.spacing(1),
    },
    instructions: {
      marginTop: theme.spacing(1),
      marginBottom: theme.spacing(1),
    },
  }));

function getSteps() {
    return ['Konto erstellen', 'Club Grün beitreten', 'Ausflug planen'];
  }

  function getStepContent(step: number) {
    switch (step) {
      case 0:
        return <LoginForm />;
      case 1:
        return <SignInSection />;
      case 2:
        return <PriceSection />;
      default:
        return 'Unknown step';
    }
  }

export const AssistentFlow: React.FC<Props> = () => {

    const classes = useStyles();
  const [activeStep, setActiveStep] = useState(0);
  const [skipped, setSkipped] = React.useState(new Set());
  const steps = getSteps();

  const isStepSkipped = (step: number) => {
    return skipped.has(step);
  };

  const handleNext = () => {
    let newSkipped = skipped;
    if (isStepSkipped(activeStep)) {
      newSkipped = new Set(newSkipped.values());
      newSkipped.delete(activeStep);
    }

    setActiveStep((prevActiveStep) => prevActiveStep + 1);
    setSkipped(newSkipped);
  };

  const handleBack = () => {
    setActiveStep((prevActiveStep) => prevActiveStep - 1);
  };

  const handleSkip = () => {
    if (!isStepOptional(activeStep)) {
      // You probably want to guard against something like this,
      // it should never occur unless someone's actively trying to break something.
      throw new Error("You can't skip a step that isn't optional.");
    }

    setActiveStep((prevActiveStep) => prevActiveStep + 1);
    setSkipped((prevSkipped) => {
      const newSkipped = new Set(prevSkipped.values());
      newSkipped.add(activeStep);
      return newSkipped;
    });
  };

  const handleReset = () => {
    setActiveStep(0);
  };

   return (
    <div className={classes.root}>
    <Stepper activeStep={activeStep}>
      {steps.map((label, index) => {
        const stepProps = {completed: false};
        const labelProps = {};
        if (isStepSkipped(index)) {
          stepProps.completed = false;
        }
        return (
          <Step key={label} {...stepProps}>
            <StepLabel {...labelProps}>{label}</StepLabel>
          </Step>
        );
      })}
    </Stepper>
    <div>
      {activeStep === steps.length ? (
        <div>
          <Typography className={classes.instructions}>
            All steps completed - you&apos;re finished
          </Typography>
          <Button onClick={handleReset} className={classes.button}>
            Reset
          </Button>
        </div>
      ) : (
        <div>
           <div>
               {getStepContent(activeStep)}
            </div> 
          <div>
            <Button disabled={activeStep === 0} onClick={handleBack} className={classes.button}>
              zurück
            </Button>

            <Button
              variant="contained"
              color="primary"
              onClick={handleNext}
              className={classes.button}>
              {activeStep === steps.length - 1 ? 'Abschließen' : 'weiter'}
            </Button>
          </div>
        </div>
      )}
    </div>
  </div>
   )
}
