import React, { useState } from "react"
import "react-dates/initialize"
import "react-dates/lib/css/_datepicker.css"
import {DateRangePicker} from "react-dates" 
import "bootstrap/dist/css/bootstrap.min.css"
import moment, { Moment } from "moment"



interface Props {
    dates: any
    dateChangeListener: any
}


export const DateSelector:  React.FC<Props> = (Props) => {

    const [startDate, setStartDate] = useState();
    const [endDate, setEndDate] = useState();
    const [focusedInput, setFocusedInput] = useState();

    const initStart = moment().add(7, 'd').locale('de');
    const initEnd = initStart.add(3, 'd').locale('de');

    console.log(initEnd);

    function onDateChange(start: Moment | null, end: Moment | null) {
            setStartDate(start);
            setEndDate(end);
      }

      function onFocusChange(focusedInput: any) {
        setFocusedInput(focusedInput);
      }

      const isOutsideRange = (day: any) => {

        var isMax = false;

        if(startDate){
            const isMax = day.isAfter(startDate.add(7, 'd'))
        }

        return day.isBefore(moment()) || isMax;

      }



    return (
            <div>

            <DateRangePicker
                startDatePlaceholderText={"Anreise"}
                endDatePlaceholderText={"Abreise"}
                startDate={startDate}
                startDateId="your_unique_start_date_id"
                endDate={endDate}
                endDateId="your_unique_end_date_id"
                onDatesChange={({ startDate, endDate }) => { onDateChange( startDate, endDate )}}
                focusedInput={focusedInput}
                displayFormat={() => "DD. MMMM YYYY"}
                onFocusChange={onFocusChange}
                isOutsideRange={isOutsideRange}/>    

        </div>
    )
}
