import React, { useContext } from "react";
import { Grid} from "@material-ui/core";
import { useProductsQuery, Product } from "../../../generated/graphql";
import { LoadingAnimation } from "../../pageframe/global/Messages/LoadingAnimation";
import { OfferView } from "../../homepage/sections/priceSectio/OfferView";
import { BasicContentBox } from "../../pageframe/global/StyledComponents";
import { OnboardingContext } from "../OnboardingContext";


interface Props {
}



export const ProductOffers: React.FC<Props> = () => {

    const {data, loading, error} = useProductsQuery();
    const context = useContext(OnboardingContext);

    const getOffer = (product: Product) => {
        return {
            id: product.id,
            price: product.price,
            durationMonth: product.minmonth,
            features: [
                "Über 100 Unterkünfte jederzeit nutzen",
                "Familie & Freunde kostenlos mitnehmen",
                "Vor Ort: Fahrräder, BBQ, Liegestühle, etc.",
                "Tour-Guide für Ausflüge und Aktivitäten",
                "24/7 Vor-Ort-Service bei Problemen"
            ]
        }

    }

    const handleOfferClicked = (id: string) => {
        context.setProductId(id)
    }



    if(data && data.getAllProducts){
        const products = data.getAllProducts;
        return (
                <BasicContentBox>
                    <Grid container justify="center" spacing={2} direction='row'>
                        {products.map((product) => {
                            return (                        
                            <Grid item key={product.id}>
                                <OfferView offer={getOffer(product)} onOfferClicked={handleOfferClicked}></OfferView>
                            </Grid>)
                        })}
                    </Grid>                  
                </BasicContentBox>       
           )
    }

    return (<LoadingAnimation message={"Angebote werden geladen ... "}></LoadingAnimation>)

}
