import React, { useState, useContext } from "react";
import { CardElement, useStripe, useElements } from "@stripe/react-stripe-js";
import { Button } from "@material-ui/core";
import { ErrorMessage } from "../../pageframe/global/Messages/ErrorMessage";
import { useProductsQuery, useAddPaymentMethodMutation } from "../../../generated/graphql";
import { OnboardingContext } from "../OnboardingContext";


interface Props {

}


const style =  {
    base: {
      fontSize: '16px',
      color: '#424770',
      '::placeholder': {
        color: '#aab7c4',
      },
    },
    invalid: {
      color: '#9e2146',
    }
}


export const PaymentForm: React.FC<Props> = () => {

    const stripe = useStripe();
    const elements = useElements();
    const context = useContext(OnboardingContext);

    const [addPaymentMethod] = useAddPaymentMethodMutation()

    const [error, seterror] = useState()


    const handleSubmit = async (event: React.FormEvent) => {
        // Block native form submission.
        event.preventDefault();

        if (!stripe || !elements) {
          seterror(new Error('Bazahldienst nicht erreichbar'));
          return;
        }

        // Get a reference to a mounted CardElement. Elements knows how
        // to find your CardElement because there can only ever be one of
        // each type of element.
        const cardElement = elements.getElement(CardElement);

        // Use your card Element with other Stripe.js APIs
        const {error, paymentMethod} = await stripe.createPaymentMethod({
          type: 'card',
          card: cardElement ? cardElement : {token: ""},
        });

        if (error) {
          console.log('[error]', error);
          seterror(error)
        } 
        if(paymentMethod){
        addPaymentMethod({
          variables: {
            paymentMethodId: paymentMethod?.id
          }
        })
        }        
      };


    return(
        <form onSubmit={handleSubmit}>
            <CardElement options={{ style: style }}>
                <Button type="submit" disabled={!stripe}></Button>
            </CardElement> 
            {error ? <ErrorMessage error={error} show={error ? true : false}/> : <div></div>
        }
        </form>

    )


}
