import React, { useState, useEffect } from 'react'
import { Property, useSavePropertyMutation, useGetPropertyQuery } from '../../../../generated/graphql'
import { useParams } from 'react-router-dom';
import { Editor } from '../../form/Editor';
import { MessageView } from '../../../pageframe/global/Messages/MessageView';
import { LoadingAnimation } from '../../../pageframe/global/Messages/LoadingAnimation';
import { ErrorMessage } from '../../../pageframe/global/Messages/ErrorMessage';


interface Props {

}

export const newProperty: Property = { 
                    id: 0,
                    title: "",
                    subtext: "",
                    location: "",
                    lat: 0.0,
                    long: 0.0,
                    beds_adult: 0,
                    beds_kids: 0,
                    beds_tent: 0,
                    images: [],   
                }

export const PropertyEditor: React.FC<Props> = (props) => {

    const { propertyId } = useParams()
    console.log(propertyId);

    const [showMessage, setshowMessage] = useState(false);

    const {data, loading, error, refetch} = useGetPropertyQuery({
        variables: {
            id: parseInt(propertyId ? propertyId : "0")
        }
    });

    useEffect(() => {
        if(propertyId){
            const isRefechted = refetch({id: parseInt(propertyId)})
            console.log("refetched" + propertyId +" "+ isRefechted)
        } 

    }, [propertyId])

    const [saveProperty] = useSavePropertyMutation();

    if(error){
        return (<ErrorMessage error={error} show={true}/>) 
    }

    async function onSubmit(values: Property) {
        values.__typename = undefined;
        values.lat = parseFloat(values.lat+"");
        values.long = parseFloat(values.long+"");
        values.beds_adult = parseFloat(values.beds_adult+"");
        values.beds_kids = parseFloat(values.beds_kids+"");
        values.beds_tent = parseFloat(values.beds_tent+"");

        var result = await saveProperty({
            variables: {
                property: values
            }
        });
        if(result){
            setshowMessage(true)
        }
    }

    var property = newProperty;
    if(data && data.getProperty) {
        console.log("called")
        property = data.getProperty
        return(
            <Editor 
                entity={property} 
                store={onSubmit} 
                entityId={property.id}
                imageuploadurl={"http://localhost:4000/upload/property/image"}>     
                <MessageView severity={"success"} message={"Die Daten wurden gespeichert"} show={showMessage} />
            </Editor>
        )   
    }

    return (<LoadingAnimation message={"Daten werden geladen ..."}/>)
}
