import React, { useState } from 'react'
import { newProperty } from './PropertyEditor';
import { Formik } from 'formik';
import * as Yup from 'yup'
import axios from 'axios'


interface Props {
    propertyId: number
}

export const PictureUpload: React.FC<Props> = (props) => {

    const [selectedFile, setSelectedFile] = useState()


    const onSubmitHandler = (file: any) => {
        const data = new FormData()
        data.append('file', file)
        data.append('prop', props.propertyId+"")
        axios.post("http://localhost:4000/upload/property/image", data, { 
            headers: {
                'Content-Type': 'multipart/form-data'
              }
        })
    }

    return(
        <Formik 
          initialValues={{myImage: null}}
          onSubmit={(values) => {  
            if(values && values.myImage){
                onSubmitHandler(values.myImage)
                console.log(values.myImage)

            } 
          }} 
          validationSchema={Yup.object().shape({
            file: Yup.mixed().required(),
          })}
          render={({ values, handleSubmit, setFieldValue }) => {
            return (
              <form onSubmit={handleSubmit}>
                <div className="form-group">
                  <input id="file" name="myImage" type="file" onChange={(event) => {
                      if(event?.currentTarget && event.currentTarget.files)
                    setFieldValue("file", event.currentTarget.files[0]);
                  }} className="form-control" />

                </div>
                <button type="submit" className="btn btn-primary">submit</button>
              </form>
            );
          }} />
    )
}
