import React, { useState } from 'react'
import { useMyBookingQuery, useCreateMyBookingMutation } from '../../generated/graphql'
import { MyBooking } from './MyBooking';
import { PropertyList } from './PropertyList';

interface Props {

}


export const BookingRouter:  React.FC<Props> = () => {

    const [selectedProp, setSelectedProp] = useState()
    const [bookingActive, setBookingActive] = useState(false)
    const [arrivalDate, setArrivalDate] = useState(new Date(Date.now())) 
    const [departureDate, setDepartureDate] = useState(new Date(Date.now()))

    const [booking] = useCreateMyBookingMutation();

    const {data, loading, error} = useMyBookingQuery();
    if(data && data?.myBooking){
        setBookingActive(true)
      setArrivalDate(new Date(data.myBooking.from)) 
      setDepartureDate(new Date(data.myBooking.to)) 
      if(data.myBooking.property){
        setSelectedProp(data.myBooking.property)
      }
    }

    const bookPropListener = (from: Date, to: Date) =>{
        book(to.getTime(), from.getTime());
    }

    const cancleBookingListener = () =>{
        console.log("booking canceled")
    }

    async function book(from: number, to: number) {
        if(selectedProp){
            const response = await booking({
                variables: {
                    from: from,
                    to: to,
                    propertyId: selectedProp.id
                }
            })
            console.log(response)
        }
    }

    const selectPropListener = (property: any) =>{
        setSelectedProp(property);
    }


    if(loading){
        return <div>
            loading ....
        </div>
    }

    if(error){
        console.log(error)
        return <div>err</div>
    }

    if(!data){
        return <div>
            no data
        </div>
    }



    return (
        <div>
            <MyBooking 
                bookingListener={bookPropListener} 
                cancleListener={cancleBookingListener}
                arrivalDate={arrivalDate} 
                departureDate={departureDate} 
                property={selectedProp}
                booking={bookingActive}></MyBooking>
            <PropertyList selectPropListener={selectPropListener}></PropertyList>
        </div>
    )

}
