import React, { useState } from 'react'
import { MyBooking } from './myBooking/MyBooking';
import { PropertyList } from './propertyList/PropertyList';

interface Props {

}


const emptyBooking = {
    selectedProperty: {},
    arrivalDate: {},
    departureDate: {},
}

export const BookingContext = React.createContext(emptyBooking);


export const BookingRouter:  React.FC<Props> = () => {

    const [activeBookingProp, setActiveBookingProp] = useState();
    const [propertySelected, setPropertySelected] = useState()

    const bookingActiveListener = (id: number) =>{
        if(id){
            setActiveBookingProp(id);

        } else {
            setPropertySelected(undefined);
        } 
    }

    const selectedPropertyListener = (property: any) =>{
        setPropertySelected(property);
        console.log("property selected " + property.id)
    }

    return (
        <div>
            <BookingContext.Provider value={emptyBooking}>
                <MyBooking ></MyBooking>
                <PropertyList ></PropertyList>
            </BookingContext.Provider>
            <MyBooking propertySelected={propertySelected} bookingActiveListener={bookingActiveListener}></MyBooking>
            <PropertyList propertyActive={activeBookingProp} selectPropListener={selectedPropertyListener}></PropertyList>
        </div>
    )

}
