import React, { useState } from 'react'
import { useTeaserPropertiesQuery, PropObject, useSavePropertyMutation } from '../../generated/graphql'
import { PropertyForm } from './PropertyForm';
import { LoadingAnimation } from '../elements/LoadingAnimation';
import { ErrorView } from '../elements/ErrorView';
import { SectionContainer, BasicContentBox } from '../StyledComponents';
import { Button } from '@material-ui/core';

interface Props {

}

export const AdminPage: React.FC<Props> = () => {

    var initProp: PropObject = { beds_adult: 0,
        beds_kids: 0,
        beds_tent: 0,
        title: "",
        id: 0,
        images: [],
        lat: 0.0,
        location: "",
        long: 0.0,
        subtext: ""
        }

    const [selectedProp, setSelectedProp] = useState();
    const {data, loading, error} = useTeaserPropertiesQuery();
    const [saveProperty] = useSavePropertyMutation();

    if(!selectedProp){
        setSelectedProp(initProp)
    }

    function propSelected (prop: PropObject) {
        setSelectedProp(prop)
    }

    if(loading){
        return (
            <LoadingAnimation />
        )
    }

    if(error){
        return (
            <ErrorView error={error}/>
        )
    }

    async function onSubmit(values: PropObject) {
        console.log(values)

        var val: PropObject = { beds_adult: values.beds_adult,
        beds_kids: values.beds_kids,
        beds_tent: values.beds_tent,
        title: values.title,
        id: values.id,
        images: values.images,
        lat: values.lat,
        location: values.location,
        long: values.long,
        subtext: values.subtext
        }


        var result = await saveProperty({
            variables: {
                property: val
            }
        });
        console.log(result)
    }

    if(data && data.teaserProperties){
        return(
            <div>
                <SectionContainer>
                    <BasicContentBox>
                        <PropertyForm property={selectedProp} onSubmit={onSubmit}/>
                    </BasicContentBox> 
                </SectionContainer>
                <ul>
                {data?.teaserProperties.map(x => {
                return <li key={x.id}> {x.id}, {x.title}, {x.location} 
                    <Button onClick={() => {propSelected(x)}}>ausw√§hlen</Button></li> })
                }
                </ul>               
            </div>
        )

    }

    return(
        <div>asdf</div>
    )


    /*
    const {data} = useAllBookingQuery()

    return (<div>

        <div>Bookings</div>

    </div>)
    */

}
