import React, { useContext, useMemo} from 'react'
import { useMyBookingQuery, useDeleteBookingMutation, useCreateMyBookingMutation, MyBookingQuery} from '../../../generated/graphql';
import { DateSelector } from './DateSelector';
import { PropertySelectionView } from './PropertySelectionView';
import { BookingButton } from './BookingButton';
import { Grid } from '@material-ui/core';
import moment from 'moment';
import { BookingContext } from '../BookingContext';
import { MyBookingArea, BasicContentBox } from '../../StyledComponents';

interface Props {

}

export const MyBooking:  React.FC<Props> = () => {

    const {data, loading, error, refetch} = useMyBookingQuery();

    const [Booking] = useCreateMyBookingMutation();
    const [deleteBooking] = useDeleteBookingMutation();
    const { 
              setArrivalDate, 
              setDepartureDate,
              selectedProperty,
              arrivalDate, 
              departureDate,
              isActive,
              setIsActive} = useContext(BookingContext);


    if(loading){
      console.log("laoding" + loading)
    }

    if(error){
      console.log("error" + error) 
    }

    /*
    const booking = data?.myBooking;
    var bookingArrival: number = 0;
    var bookingDeparture: number = 0;
    var property: Property | undefined = undefined;
    if(booking && booking?.property) {
        bookingArrival = booking.from;
        bookingDeparture = booking.to;
        property = booking.property
    }
    useMemo(() => {      
          if(booking && booking.from && booking.to){
            setArrivalDate(moment(bookingArrival));
            setDepartureDate(moment(bookingDeparture))
            setIsActive(true);

            if(booking.property){
              setSelectedPropterty(property);
            }
          }
    }, [bookingArrival, bookingDeparture, booking, property, setArrivalDate, setDepartureDate, setIsActive, setSelectedPropterty]) 

    */

   const setBooking = (data: MyBookingQuery | undefined) => {
    const myBooking = data?.myBooking;
    if(myBooking && myBooking.from && myBooking.to){
      setArrivalDate(moment(myBooking.from));
      setDepartureDate(moment(myBooking.to))
      setIsActive(true);
    }
  }
    useMemo(() => {      
          setBooking(data)
    }, []) 



    const bookingButtonPressed = async () => {
      if(isActive){
          deleteBook();     
      } else{
          createBook()
      }
    }

    const deleteBook = async () => {
      if(data && data.myBooking){
        const result = await deleteBooking({
          variables: {
            bookingId: data.myBooking.id
          }
        })
        if(result){
          console.log("result myBooking" + result);
          refetch();
        }
      } 
    }

    const createBook = async () => {
      if(selectedProperty && arrivalDate && departureDate){
        const result = await Booking(
          { 
            variables: {
                from: arrivalDate.unix(),
                to: departureDate.unix(),
                propertyId: selectedProperty.id
            }
          })
          if(result){

            console.log(result);
            refetch();
          }  
      }
    }


    return (
        <div>
              <MyBookingArea>        
                <BasicContentBox>
                  <Grid container spacing={4}>
                    <Grid item xs={12}>
                      <DateSelector />
                    </Grid>
                    <Grid item xs={9}>
                      <PropertySelectionView />
                    </Grid>
                    <Grid item xs={3}>
                      <BookingButton aktiveBooking={isActive} buttonPressedListener={bookingButtonPressed}/>
                    </Grid>
                  </Grid>
                  {}
                  </BasicContentBox>               
              </MyBookingArea>             
          </div>
    )

}
