import React from "react";
import { Address, AddressForm } from "../../components/addressform";
import { useMeQuery, useUpdateUserDataMutation } from "../../generated/graphql";
import { ErrorMessage } from "../../pages/pageframe/global/Messages/ErrorMessage";
import { NameForm, NameData } from "../../components/nameform";
import { LoadingAnimation } from "../../pages/pageframe/global/Messages/LoadingAnimation";

interface Props {

}

export const PersonalDataEditor: React.FC<Props> = () => {

    const {data, loading, error} = useMeQuery();
    const [userdata] = useUpdateUserDataMutation();

    if(error){
        return (<ErrorMessage error={error} show={true}/>)
    }


    const handleSaveAddress = (address: Address) => {
        userdata({
            variables: {
                useraddress: address,
                username: null
            }
        })
    }

    const handleSaveName = () => {

    }


    if(data && data.me){
        const user = data.me;

        const address: Address = {
            streetname: "",
            streetnumber: "",
            postal: "",
            town: ""  
        }

        const name: NameData ={
            gender: "male",
            first_name: "Sven",
            surename: "Lehneke"
        }



        return (
            <div>
                <NameForm values={name} onSubmit={handleSaveAddress}/>
                <AddressForm values={address} onSubmit={handleSaveAddress}/>
            </div>

        )
    }
    return (
        <LoadingAnimation message={"Daten werden geladen"} />
    )
}
