import React, {useState} from "react";
import { useLoginMutation, MeDocument, MeQuery } from "../../../../generated/graphql";
import { useHistory } from "react-router-dom";
import { setAccessToken } from "../../../../accessToken";
import { TextField, Button, Typography } from "@material-ui/core";
import { SectionContainer, BasicContentBox } from "../StyledComponents";
import './styles.css'

interface Props {

}

export const Login: React.FC<Props> = () => {
    const [email, setEmail] = useState("");
    const history = useHistory();
    const [password, setPassword] = useState("");
    const [responseValues, setResponseValue] = useState("");
    const [Login] = useLoginMutation()


    return (
        <SectionContainer maxWidth="xs">
            <BasicContentBox>
        <div style={{width: '40%', margin: 'auto'}}>
            <form onSubmit={async e => {
                e.preventDefault()

                try{
                    const response = await Login({
                        variables: {
                            email, password
                        },
                        update: (store, {data}) => {
                            if(!data){
                                return null;
                            }
                            store.writeQuery<MeQuery>({
                                query: MeDocument,

                                data: {
                                    me: data.login.user
                                }
                            })  
                        }
                    })
                    if(response && response.data){
                        setAccessToken(response.data.login.accessToken)
                    }
                    setResponseValue("");
                    history.push('/booking');
                    console.log(response)


                } catch(err){  
                    if(err.message) {
                        setResponseValue("Nutzername oder Password nicht richtig.");
                    }
                }  

            }} >
                <div>
                    <Typography color="error" style={{margin: 'auto'}}>
                        {responseValues}
                    </Typography>

                </div>
                <div>
                    <TextField 
                        fullWidth={true}
                        id="standard-basic" 
                        label="Email"
                        value={email} 
                        onChange={e => {
                            setEmail(e.target.value)
                            }
                        } 
                    />
                </div>
                <div>
                    <TextField 
                        fullWidth={true}
                        id="standard-basic" 
                        label="Password"
                        type="password"
                        value={password} 
                        onChange={e => {
                            setPassword(e.target.value)
                            }
                        } 
                    />
                </div>
                <div >
                    <Button variant={"outlined"} style={{margin: 'auto', display: 'block', marginTop: '24px'}} type="submit">anmelden</Button>
                </div>   
            </form>
        </div>
        </BasicContentBox>
    </SectionContainer>
    )
};
