import React, { useState } from 'react'
import { newProperty, PropertyEditor } from './PropertyEditor';
import { PropertyList } from './PropertyList';
import { Property } from '../../generated/graphql';
import { Grid } from '@material-ui/core';
import { SectionContainer, BasicContentBox } from '../pageframe/global/StyledComponents';

interface Props {

}

export const AdminPage: React.FC<Props> = () => {

    const [selectedProperty, setSelectedProperty] = useState(newProperty)

    const onPropertySelected = (property: Property) => {
        console.log(property);
        setSelectedProperty(property);
    }



    return(
        <SectionContainer>
            <BasicContentBox>
                <div style={{width: "30%", margin: "8px"}}>
                    <PropertyList onPropertySelected={onPropertySelected}/>
                </div>
                <div style={{width: "30%", margin: "8px"}}>
                    <PropertyEditor key={selectedProperty.id} property={selectedProperty}/>
                </div>
            </BasicContentBox>
        </SectionContainer>
    )
}
