import React from "react";
import { Product } from "../../generated/graphql";
import { PaymentMethod } from "@stripe/stripe-js";


type Address = {
    street:  string, 
    number: number, 
    postalCode: string, 
    city: string }

type PersonData = {
    firstName: string,
    surname: string,
    phoneNumber: string
}


type OnboardingCtxt = {
    step: number,
    product: Product | undefined
    paymentMethod: PaymentMethod | undefined
    address: Address | undefined
    personData: PersonData | undefined
    setStep: (step: number) => void 
    setProduct: (product: Product) => void 
    setPaymentMethod: (product: PaymentMethod) => void 
}

export const initialOnboarding: OnboardingCtxt = {
    step: 0,
    product: undefined,
    paymentMethod: undefined,
    address: undefined,
    personData: undefined,
    setStep: (step: number) => {},
    setProduct: (product: Product) => {},
    setPaymentMethod: (product: PaymentMethod) => {}
}

export const OnboardingContext = React.createContext<OnboardingCtxt>(initialOnboarding);
