import { MuiPickersUtilsProvider, KeyboardDatePicker } from "@material-ui/pickers"
import DateFnsUtils from "@date-io/date-fns"
import { Grid } from "@material-ui/core"
import React, { useState } from "react"
import "react-dates/initialize"
import "react-dates/lib/css/_datepicker.css"
import {DateRangePicker} from "react-dates" 


interface Props {
    dates: any
    dateChangeListener: any
}


export const DateSelector:  React.FC<Props> = (Props) => {

    const [arrival, setArrival] = useState(Props.dates.arrival);
    const [departure, setDeparture] = useState(Props.dates.arrival)
    const [dates, setDates] = useState({arrival: null, departure: null});

    const setDateOfArrivial = (date: any) => {
        const arrivalDate = new Date(date);
        setArrival(arrivalDate.getTime())
        Props.dateChangeListener({arrival: arrival, departure: departure});
    }

    const setDateOfDeparture = (date: any) => {
        const departureDate = new Date(date);
        setDeparture(departureDate.getTime())
        Props.dateChangeListener({arrival: arrival, departure: departure});      
    }

    const onChangeListener = (start: any, end: any) => {
        setDates({arrival: start, departure: end});
    }

    return (
        <div>
            <DateRangePicker
                startDate={dates.arrival} // momentPropTypes.momentObj or null,
                startDateId="your_unique_start_date_id" // PropTypes.string.isRequired,
                endDate={dates.departure} // momentPropTypes.momentObj or null,
                endDateId="your_unique_end_date_id" // PropTypes.string.isRequired,
                onDatesChange={onChangeListener} // PropTypes.func.isRequired,
                focusedInput={this.state.focusedInput} // PropTypes.oneOf([START_DATE, END_DATE]) or null,
                onFocusChange={focusedInput => this.setState({ focusedInput })} // PropTypes.func.isRequired,
                />
            <MuiPickersUtilsProvider utils={DateFnsUtils}>
            <Grid container spacing={2}>
                <Grid item>
                    <KeyboardDatePicker
                        disableToolbar
                        variant="inline"
                        format="dd.MM.yyyy"
                        margin="normal"
                        id="date-picker-inline"
                        label="Ankunft"
                        value={arrival}
                        onChange={setDateOfArrivial}
                        KeyboardButtonProps={{
                            'aria-label': 'change date',
                        }}
                    />
                </Grid>
                <Grid item>
                    <KeyboardDatePicker
                        disableToolbar
                        variant="inline"
                        margin="normal"
                        id="date-picker-inline"
                        label="Abreise"
                        format="dd.MM.yyyy"
                        value={departure}
                        onChange={setDateOfDeparture}
                        KeyboardButtonProps={{
                            'aria-label': 'change date',
                        }}/>
                </Grid>
            </Grid>
            </MuiPickersUtilsProvider> 
        </div>
    )
}
