import React, { ReactNode } from 'react'
import LocalHotelIcon from '@material-ui/icons/LocalHotel'

interface Props {
    propertyFeatures: {
        beds_adult: 0, 
        beds_kids: 0,
        beds_tent: 0
    }
    children: ReactNode
}

export const FeaturesBatch:  React.FC<Props> = (props) => {

    const beds_adult = () => {
        if(props.propertyFeatures.beds_adult){
            return (
                <div style={{display: 'inline', margin: '8px'}}>
                    <LocalHotelIcon /> {props.propertyFeatures.beds_adult}
                </div>              
            )
        }
        return(<div></div>)
    }
    const beds_kids = () => {
        if(props.propertyFeatures.beds_adult){
            return (
                <div style={{display: 'inline', margin: '8px'}}>
                    <LocalHotelIcon /> {props.propertyFeatures.beds_adult}
                </div>              
            )
        }
    }

    return (
        <div>
            {props.children}
            {beds_adult()}
            {beds_kids()}
        </div>
    )
}
