import React from 'react'
import DateFnsUtils from '@date-io/date-fns';
import { Grid, Button } from '@material-ui/core';
import {
    MuiPickersUtilsProvider,
    KeyboardDatePicker,
  } from '@material-ui/pickers';

interface Props {
    mybooking: any
    listener: any
    property: any
}


export const MyBooking:  React.FC<Props> = (Props) => {

  // const [propTitle, setPropTitle] = useState(undefined)

    const d = new Date(Date.now())
    const [selectedStartDate, setSelectedStartDate] = React.useState<Date | null>(
        d
        //new Date('2014-08-18T21:11:54'),
      );
      const [selectedEndDate, setSelectedEndDate] = React.useState<Date | null>(
        d
        //new Date('2014-08-18T21:11:54'),
      );

      // setPropTitle(Props.property.title);

    const handleStartDateChange = (date: Date | null) => {
        setSelectedStartDate(date);
      };

      const handleEndDateChange = (date: Date | null) => {
        setSelectedEndDate(date);
      };

      const saveBooking = () => {
          Props.listener(selectedStartDate, selectedStartDate)
      } 

    return (
        <div>
           <MuiPickersUtilsProvider utils={DateFnsUtils}>
            <Grid container>
                <KeyboardDatePicker
                disableToolbar
                variant="inline"
                format="dd.MM.yyyy"
                margin="normal"
                id="date-picker-inline"
                label="Ankunft"
                value={selectedStartDate}
                onChange={handleStartDateChange}
                KeyboardButtonProps={{
                    'aria-label': 'change date',
                }}
                />
                <KeyboardDatePicker
                margin="normal"
                id="date-picker-dialog"
                label="Abreise"
                format="dd.MM.yyyy"
                value={selectedEndDate}
                onChange={handleEndDateChange}
                KeyboardButtonProps={{
                    'aria-label': 'change date',
                }}
                />
                <Button onClick={saveBooking}>
                    Reservieren
                </Button>
            </Grid>
            </MuiPickersUtilsProvider>
            <div>
              {Props.property.title}
              </div>
        </div>
    )

}
