import React, { useState } from 'react'
import { Button, TextField, Grid } from '@material-ui/core'
import { Formik , Form} from 'formik'
import { ImageList } from '../imagehandler/ImageList'

interface Props {
    values: any;
    onSubmit: (values: any) => void;
}

export const EditorForm: React.FC<Props> = (props) => {

    const [onChange, setonChange] = useState()

    const onDeleteImage = (imageUrl: string) => {

    }

    return (
        <Formik initialValues={props.values} onSubmit={props.onSubmit} enableReinitialize={true}>
            {({values, handleChange, handleBlur}) => (
                <Form>
                <Grid container direction="column" spacing={4}>
                        {Object.entries(values).map(([key, value]) => {
                            if(key == "images"){
                                if(value && value instanceof Array) {
                                    if(value[0] && typeof value[0] == 'string' ){
                                        return <ImageList 
                                            imageUrlApends={value} 
                                            onDeleteHandler={onDeleteImage}></ImageList> 
                                    } 
                                    console.log("no values or no string") 
                                }
                                console.log("images undefined or no Array") 
                            } else {
                                return <Grid item key={key}>
                                            <TextField 
                                            name={key}
                                            label={key}
                                            value={value ? value : ""} 
                                            onChange={handleChange}
                                            onBlur={handleBlur}
                                            />
                                        </Grid>
                            }
                            }
                        )} 
                        <Grid item>
                            <Button variant="contained" type="submit">
                                speichern
                            </Button>
                        </Grid>
                    </Grid> 
                </Form>  
            ) }           
        </Formik>
    )


}

