import React from 'react'
import moment, { Moment } from 'moment'
import { useBookingsByPropertyQuery } from '../../../generated/graphql'

interface Props {

}


export const DateControl:  React.FC<Props> = () => {

    const {data, loading, error} = useBookingsByPropertyQuery({variables:{propertyId: 1}})

    console.log(loading);
    console.log(error);

    console.log(data?.activeBookingsForProperty);

    const generateBlockedDaysList = async () => {

        var blockedDays: Moment[] = [];
        if(data && data.activeBookingsForProperty){

            console.log(data?.activeBookingsForProperty);

          data.activeBookingsForProperty.map((dateRange) => {
              var start = moment(dateRange.from).startOf("d");
              do{               
                  blockedDays.push(start);
                  start = start.add(1, "day");               
              } while(!start.isAfter(moment(dateRange.to)))

              return dateRange;
          })

        }

        return blockedDays;
    }

    const dates = async () => { 
        try{ var list = await generateBlockedDaysList();
            return list[0];
        }
        catch (error){
            return 0
        };
    }

    console.log(dates)

    return (<div>
        keine Daten
    </div>)
}
