import React from "react";
import { useLoginMutation, MeDocument, MeQuery } from "../../../../generated/graphql";
import { setAccessToken } from "../../../../accessToken";
import { TextField, Button, Checkbox, Typography} from "@material-ui/core";
import {Formik} from 'formik'
import * as Yup from 'yup'
import { useHistory } from "react-router-dom";

interface Props {

}

export const LoginForm: React.FC<Props> = () => {
    const history = useHistory();
    const [Login] = useLoginMutation()

    const loginFunc = async (values: {email: string, password: string}) => {
        const response = await Login({
            variables: {
                email: values.email, 
                password: values.password
            },
            update: (store, {data}) => {
                if(!data){
                    return null;
                }
                store.writeQuery<MeQuery>({
                    query: MeDocument,
                    data: {
                        me: data.login.user
                    }
                })  
            }
        })
        if(response && response.data){
            setAccessToken(response.data.login.accessToken)
            history.push('/booking')
        }
    }

    return (
        <div style={{maxWidth: '300px', margin: 'auto'}}>
            <Formik initialValues={{
                email: "",
                password: "",
                acceptTerms: false
            }} 
            onSubmit={loginFunc}
            validationSchema={Yup.object().shape({
                email: Yup.string()
                  .email()
                  .required("Required"),
                  password: Yup.string().min(8).required("Required"),
                  acceptTerms: Yup.bool().oneOf([true]).required()
              })} >
                {props => {
                const {
                values,
                touched,
                errors,
                handleChange,
                handleBlur,
                handleSubmit,
                } = props;
                return (
                    <form onSubmit={handleSubmit}>
                            <TextField 
                                error={errors.email && touched.email
                                    ? true
                                    : false
                                }
                                fullWidth={true}
                                id="email" 
                                label="Email"
                                value={values.email} 
                                onChange={handleChange} 
                                onBlur={handleBlur}
                                helperText={errors.password && touched.password 
                                    ? "Gib eine g체ltige Email-Adresse an."
                                    : ""
                                }
                            />
                            <TextField 
                                error={errors.password && touched.password
                                    ? true
                                    : false
                                }
                                fullWidth={true}
                                id="password" 
                                label="Passwort"
                                type="password"
                                value={values.password} 
                                onChange={handleChange}
                                onBlur={handleBlur}
                                helperText={errors.password && touched.password 
                                    ? "Dein Passwort muss mindestens 8 Zeichen lang sein."
                                    : ""
                                }
                            />
                            <Typography>
                            <Checkbox
                                    checked={values.acceptTerms}
                                    id="acceptTerms"
                                    onChange={handleChange}
                                    inputProps={{ 'aria-label': 'primary checkbox' }}
                                /> Bitte best채tige unsere <a>Nutzungsbedingungen</a> und die <a>Erkl채rung zum Datenschutz</a>.
                            </Typography>

                            <Button variant={"outlined"} style={{margin: 'auto', display: 'block', marginTop: '24px'}} type="submit">anmelden</Button>                          
                    </form>);
            }}
            </Formik>
            </div>)
};
