import React from 'react'
import { Button, TextField, Select, MenuItem } from '@material-ui/core'
import { Formik , Form} from 'formik'
import styles from "./NameForm.module.css"
import * as Yup from 'yup'


export interface NameData {
    gender: "male" | "female" | "else"
    first_name: string
    surename: string
}

interface Props {
    values: NameData;
    onSubmit: (values: NameData) => void;
}

export const NameForm: React.FC<Props> = (props) => {
    return (
        <Formik initialValues={props.values} 
            onSubmit={props.onSubmit} 
            enableReinitialize={true}
            validationSchema={
                Yup.object().shape({
                    streetname: Yup.string().required("Required"),
                    streetnumber: Yup.string().required("Required"),
                    postal: Yup.string().min(8).required("Required"),
                    town: Yup.string().required("Required"),
              })}>
            {({values, handleChange, handleBlur}) => (
                <Form>
                    <div className={styles.row_elements}>
                        <Select
                        style={{width: "160px", justifySelf: "flex-start"}} 
                        name={values.gender}
                        label={"Straße"}
                        value={values.gender} 
                        onChange={handleChange}
                        onBlur={handleBlur}
                        >
                            <MenuItem value={"male"}>Mann</MenuItem>
                            <MenuItem value={"female"}>Frau</MenuItem>
                            <MenuItem value={"else"}>Sonstige</MenuItem>
                        </Select>
                    </div>
                    <div className={styles.row_elements}>
                        <TextField 
                        style={{flex: 1}}
                        name={values.first_name}
                        label={"Vorname"}
                        value={values.first_name} 
                        onChange={handleChange}
                        onBlur={handleBlur}
                        />
                        <TextField 
                        style={{flex: 1, marginLeft: "40px"}}
                        name={" "+values.surename}
                        label={"Nachname"}
                        value={values.surename} 
                        onChange={handleChange}
                        onBlur={handleBlur}/>
                    </div>      
                <Button variant="outlined" type="submit">
                    speichern
                </Button>
                </Form>  
            ) }           
        </Formik>
    )
}
